import { ChainId } from "../src";

import { initClients } from "./initClient";

const [, , commonClient] = initClients();
const creationHash = "";
const giveChain = ChainId.Solana;

const fulfillerAddress: Buffer = Buffer.from("");
const giveChainUnlockAuthority: Buffer = Buffer.from("");

const order = await commonClient.getOrderFromTransaction({ giveChain, txHash: creationHash }, {});
if (order === null) throw new Error(`Failed to get order from tx: ${creationHash}`);
const fulfillTx = await commonClient.fulfillOrder(
  { order },
  { permit: "0x", taker: fulfillerAddress, unlockAuthority: giveChainUnlockAuthority },
);

